function hyphenate_titles(){if(my_titles_ul=document.getElementById("my-titles"),null!=my_titles_ul){my_titles_lis=my_titles_ul.childNodes;var t=3;for(t=3;t<my_titles_lis.length;t+=2){var e=my_titles_lis[t-2].getBoundingClientRect();if(my_titles_lis[t].getBoundingClientRect().top==e.top){var l=document.createTextNode(" - ");my_titles_ul.insertBefore(l,my_titles_lis[t]),t+=1}}}else console.log("My titles were not found.")}function init(){hyphenate_titles()}onload=init;