function hyphenate_titles(){if(my_titles_ul=document.getElementById("my-titles"),null!=my_titles_ul){my_titles_lis=my_titles_ul.childNodes;var t=3;for(t=3;t<my_titles_lis.length;t+=2){var e=my_titles_lis[t-2].getBoundingClientRect(),l=my_titles_lis[t].getBoundingClientRect();if(l.top==e.top){var i=document.createTextNode(" - ");my_titles_ul.insertBefore(i,my_titles_lis[t]),t+=1,console.log(e,l);var l=my_titles_lis[t].getBoundingClientRect();l.top!=e.top&&(my_titles_ul.removeChild(my_titles_lis[t-1]),line_break=document.createElement("br"),my_titles_ul.insertBefore(line_break,my_titles_lis[t-1]))}}}else console.log("My titles were not found.")}function init(){hyphenate_titles()}onload=init;